var tl = gsap.timeline({
        onComplete: myFunction
    }),
    wineH = $(window).width() * 0.05,
    H = $(window).height() - (wineH * 10),
    fromH = H - (wineH * 0.4);

function myFunction() {
    tl.kill()
}
tl.to(".wine", {
    opacity: 1,
    duration: .4
}).to(".wine", {
    ease: "bounce.out",
    y: "random(" + fromH + ", " + H + ")",
    rotation: "random(-180, 180)",
    duration: 2,
    stagger: {
        amount: .4,
        from: "random"
    }
}), tl.pause();